import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/services/api-service.service';
import { FormGroup, FormControl} from '@angular/forms';

@Component({
  selector: 'app-myclans',
  templateUrl: './myclans.component.html',
  styleUrls: ['./myclans.component.css']
})
export class MyclansComponent implements OnInit {

  clans: any = [];

  editFormGroup: FormGroup = new FormGroup({
    name: new FormControl(),
    desc: new FormControl(),
  });

  modal: boolean = false;

  constructor(private api: ApiService) { }

  ngOnInit(): void {
    this.api.getMyClans().subscribe((data: any) => {
      if (data.hasOwnProperty("error")) {
        alert(data.error);
      }
      else {
        this.clans = data["clans"];
      }
    });
    console.log(this.modal);
  }

  openModal(id: any) {
    this.editFormGroup.controls['name'].setValue(this.clans[id].name);
    this.editFormGroup.controls['desc'].setValue(this.clans[id].desc);
    this.modal = true;
  }

  closeModal() {
    this.modal = false;
  }

}
