import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-clancard',
  templateUrl: './clancard.component.html',
  styleUrls: ['./clancard.component.css']
})
export class ClancardComponent implements OnInit {
  @Input() clan: any;

  name: string = "";
  id: number = 0;
  desc: string = "";

  constructor() { }

  ngOnInit() {
    this.name = this.clan.name;
    this.id = this.clan.id;
    this.desc = this.clan.desc;
  }

  onSave() {
    console.log("save");
  }

  onDelete() {
    console.log("delete");
  }

}
