import { Component, Input } from '@angular/core';
import { ApiService } from '../../services/api-service.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {
  @Input() entity?: string;
  @Input() nFollowers?: number = 0;
  @Input() nFollowing?: number = 0;
  @Input() nPosts?: number = 0;
  @Input() nComments?: number = 0;
  @Input() imageUrl?: string;
  @Input() name?: string;
  @Input() desc?: string;
  @Input() labels?: string[]; // ['Clan 1', 'Clan 2', 'Clan 3', 'Clan 4', 'Clan 5']
  @Input() data: number[] = []; // [12, 19, 3, 5, 2, 3]
  @Input() follow?: boolean = false;
  @Input() clanId?: string;

  constructor(private api:) { }

  followClan() {
    this.follow = true;
  }

  unfollowClan() {
    this.follow = false;
  }
}
