import { Component } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';
import { Router } from '@angular/router';
import { SearchresultsComponent } from '../../searchresults/searchresults.component';
import { ApiService } from 'src/app/services/api-service.service';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {

  searchForm: FormGroup | any;
  searchComponent: SearchresultsComponent = new SearchresultsComponent(this.api, this.router);

  constructor(private router: Router) {
    this.searchForm = new FormGroup({
      search: new FormControl()
    });
  }

  search() {
    if (this.router.url == '/searchresults') {
      this.searchComponent.doSearch("search="+this.searchForm.value['search']);
    }
    this.router.navigate(['/searchresults', { search: this.searchForm.value['search'] }]);
  }
}
