import { Component, Input, OnInit } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';
import { ApiService } from 'src/app/services/api-service.service';

@Component({
  selector: 'app-clancard',
  templateUrl: './clancard.component.html',
  styleUrls: ['./clancard.component.css']
})
export class ClancardComponent implements OnInit {

  @Input() clan: any;

  constructor(private api: ApiService ) { }

  ngOnInit() {
  }

  onSave() {
    this.api.editClan({}).subscribe((data: any) => {
      if (data.hasOwnProperty("error")) {
        alert(data.error);
      }
      else {
        alert("Clan updated");
      }
    });
  }

  onDelete() {
    this.api.deleteClan(this.clan.id).subscribe((data: any) => {
      if (data.hasOwnProperty("error")) {
        alert(data.error);
      }
      else {
        alert("Clan deleted");
      }
    });
  }

}
