import { Component, Input, OnInit } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';
import { Router } from '@angular/router';
import { ApiService } from 'src/app/services/api-service.service';

@Component({
  selector: 'app-bigform',
  templateUrl: './bigform.component.html',
  styleUrls: ['./bigform.component.css']
})
export class BigformComponent implements OnInit {
  @Input() type?: string;

  editForm: FormGroup = new FormGroup({});

  constructor(private api: ApiService) { }

  ngOnInit() {
    const token = sessionStorage.getItem("token");
    if (this.type === "user") {
      this.api.getBasicProfile(token).subscribe((data: any) => {
        this.editForm = new FormGroup({
          name: new FormControl(data.name),
          desc: new FormControl(data.bio),
          img: new FormControl(),
          background_img: new FormControl(),
          password: new FormControl(),
          password2: new FormControl(),
          password3: new FormControl(),
        });
      });
    } else if (this.type === "clan") {
    }
  }

  onSubmit() {
    console.log(this.editForm.value);
  }
}
